import datetime# Store the next idlast_id = 0class Note:	"""Represents a note in a notebook. Match agains a 	string in searches and stores tags for each note"""	def __init__(self, memo, tags=""):		"""Initializes a note woth memo and optional 		space-separeated tags. Automatically set the notes's 		creation date and a unique id"""		self.memo = memo		self.tags = tags		self.creation_date = datetime.date.today()		global last_id		last_id += 1		self.id = last_id	def match(self, filter):		"""Determine if this note matches the filter text. Return		True if matches and False otherwise		Search is case sensitive and matches both text and tags"""		return filter in self.memo or filter in self.tags